class ModelParallel(torch.nn.Module):
    def __init__(self, model_name):
        super(ModelParallel, self).__init__()
        self.model = AutoModelForCausalLM.from_pretrained(model_name)

        # Asignar partes del modelo a diferentes GPUs
        self.model.transformer.h[:12].to('cuda:0')  # Primeras capas en GPU 0
        self.model.transformer.h[12:].to('cuda:1')  # Últimas capas en GPU 1
        self.model.lm_head.to('cuda:0')  # lm_head en GPU 0

    def forward(self, input_ids):
        # Forward pass en la primera mitad del modelo en GPU 0
        with torch.cuda.device('cuda:0'):
            outputs = self.model.transformer.h[:12](input_ids.to('cuda:0'))

        # Forward pass en la segunda mitad del modelo en GPU 1
        with torch.cuda.device('cuda:1'):
            outputs = self.model.transformer.h[12:](outputs.last_hidden_state.to('cuda:1'))

        # lm_head en GPU 0
        with torch.cuda.device('cuda:0'):
            logits = self.model.lm_head(outputs.last_hidden_state.to('cuda:0'))

        return logits

